
$(document).ready(function(){
    /* search bar home page */
    $('.ic-search-tp').hide();
    $('.close-tog-ic').hide();
    $(".ic-search-tag").click(function(){
      $('.ic-search-tp').show();
      $('.close-tog-ic').show();
      
    });
    $(".close-tog-ic").click(function(){
      $('.ic-search-tp').css('display', 'none !important');
      $(this).hide();
      alert();
    });


    /* banner left right hover */
    $('.ic-banner-angle span.fa-angle-left').hover();

    /* close modal */
    $(".ic-close").click(function(){
      $(".modal").css("display", "none");
    })

});

/* Home ads load slow */
$(window).on('load', function() {
  $(".modal").delay(1000).css("display", "block");
})